import { useEffect, useRef } from 'react';
import './ProjectsTab.css';
const projectScroller = useRef<HTMLImageElement>(null);
const galleryContainer = document.querySelector('.gallery-container');
window.addEventListener('scroll', () => {
    // Get the vertical scroll amount
    const scrollY = window.scrollY;
    // Scroll the gallery horizontally based on the vertical scroll
    galleryContainer.scrollLeft = scrollY;
});

const spaceShipRef = useRef<HTMLImageElement>(null);
const shadowRef = useRef<HTMLImageElement>(null);
useEffect(() => {
  console.log("running");
  const spaceShip = spaceShipRef.current;
  const shadow = shadowRef.current;
  if (!spaceShip || !shadow) {
    return;
  }

  spaceShip.classList.add('shaking');

  let hasTakenOff = false;
  function handleScroll() {
    if (spaceShip && shadow && window.scrollY > 0) {
      spaceShip.classList.remove('shaking');
      shadow.classList.add('shadowTakeOff');
      spaceShip.classList.add('takeOff');
      hasTakenOff = true;
    } else if (spaceShip && shadow && window.scrollY == 0) {
      shadow.classList.remove('shadowTakeOff');
      spaceShip.classList.remove('takeOff');
      spaceShip.classList.add('shaking');
      hasTakenOff = true;
    }
  }
  window.addEventListener('scroll', handleScroll);
  return () => {
    window.removeEventListener('scroll', handleScroll);
  };
}, []);

function ProjectsTab() {
  return (
    <div className="projectsTab">
      <div className="borderDiv">
        <img className="border" src="pinkborder2.png" alt=""></img>
      </div>
      <div className="projectsBorder">
        <div className="titleDiv">
          <div className='wordHolder'>
            <div className="projectsTitle">Projects</div>
          </div>          
          <div className="circles">
            <img id="image" className="tinyCow" src="tinyCow.png" alt=""></img>
            <img id="image" className="tinyPlanet" src="tinyPlanet.png" alt=""></img>
            <img id="image" className="tinyLaptop" src="tinyLaptop.png" alt=""></img>
            <img id="image" className="tinyRabbit" src="tinyRabbit.png" alt=""></img>
            <div className="projectsCircle"></div>
            <div className="rightProjectsCircle"></div>
          </div>
        </div>
      </div>
      <div className='projectScroller'>
            <div className='project'>
              <img id="image" className="gif" src="highresshortvid.gif" alt=""></img>
            </div>
            <div className='project'>
              <img id="image" className="music" src="music.jpg" alt=""></img>
            </div>            
            <div className='project'>APRL</div>
            <div className='project'>JWST</div>
            <div className='project'>BALANCING ROBOT</div>
      </div>
    </div>  );
}

export default ProjectsTab;
