import { useState, useEffect } from 'react';
import './ProjectsTab.css';

function ProjectsTab() {
  // const [projectScrolling, setProjectScrolling] = useState(false);

  // useEffect(() => {
  //   if (projectScrolling === true) {
  //     disableScrolling()
  //   } else {
  //     enableScrolling()
  //   }
  // }, [projectScrolling])

  // function scrollFunction(event: any) {
  //   const div = document.querySelector('.projectsTab');
  //   const body = document.querySelector(".App");
  //   if (div) {
  //     const {top: t} = div.getBoundingClientRect();
  //     if (t < 0) {
  //       if (projectScrolling === false && body) {
  //         setProjectScrolling(true);
  //         body.scrollTop = (window.scrollY+t)
  //         // console.log(window.scrollY)

  //         console.log(body.scrollTop)
  //       }
  //     }
  //   }
  // }

  function disableScrolling() {
    const body = document.body;
    body.classList.add('noScroll');
  }

  function enableScrolling() {
    const body = document.body;
    body.classList.remove('noScroll');
  }
  return (
    <div className="projectsTab" onClick={enableScrolling}>
      <div className="borderDiv">
        <img className="border" src="pinkborder2.png" alt=""></img>
      </div>
      <div className="projectsBorder">
        <div className="titleDiv">
          <div className='wordHolder'>
            <div className="projectsTitle">Projects</div>
          </div>          
          <div className="circles">
            <img id="image" className="tinyCow" src="tinyCow.png" alt=""></img>
            <img id="image" className="tinyPlanet" src="tinyPlanet.png" alt=""></img>
            <img id="image" className="tinyLaptop" src="tinyLaptop.png" alt=""></img>
            <img id="image" className="tinyRabbit" src="tinyRabbit.png" alt=""></img>
            <div className="projectsCircle"></div>
            <div className="rightProjectsCircle"></div>
          </div>
        </div>
      </div>
      <div id="projectScroller" className='projectScroller'>
        <div className='project'>
          <img id="image" className="gif" src="highresshortvid.gif" alt=""></img>
        </div>
        <div className='project'>
          <img id="image" className="music" src="music.jpg" alt=""></img>
        </div>            
        <div className='project'>APRL</div>
        <div className='project'>JWST</div>
        <div className='project'>BALANCING ROBOT</div>
      </div>
    </div>  );
}

export default ProjectsTab;
