import './App.css';
import EducationTab from './EducationTab';
import FavoritesTab from './FavoritesTab';
import GoalsTab from './GoalsTab';
import HomeTab from './HomeTab';
import LandingTab from './LandingTab';
import OriginTab from './OriginTab';
import ProjectsTab from './ProjectsTab';
import WorkTab from './WorkTab';
import { useState, useEffect } from 'react';

function App() {
  const [projectScrolling, setProjectScrolling] = useState(false);

  useEffect(() => {
    if (projectScrolling === true) {
      disableScrolling()
    } else {
      enableScrolling()
    }
  }, [projectScrolling])

  function scrollFunction(event: any) {
    const div = document.querySelector('.projectsTab');
    const body = document.querySelector(".App");
    if (div) {
      const {top: t} = div.getBoundingClientRect();
      if (t < 0) {
        if (projectScrolling === false && body) {
          setProjectScrolling(true);
          body.scrollTop = (window.scrollY+t)
          // console.log(window.scrollY)

          console.log(body.scrollTop)
        }
      }
    }
  }

  function disableScrolling() {
    const body = document.body;
    body.classList.add('noScroll');
  }

  function enableScrolling() {
    const body = document.body;
    body.classList.remove('noScroll');
  }

  return (
    <div className="App">
      <header className="App-header">
        <HomeTab></HomeTab>
        <EducationTab></EducationTab>
        <GoalsTab></GoalsTab>
        <WorkTab></WorkTab>
        <ProjectsTab></ProjectsTab>
        <OriginTab></OriginTab>
        <FavoritesTab></FavoritesTab>
        <LandingTab></LandingTab>
      </header>
    </div>
  );
}

export default App;
