import { useEffect, useRef } from 'react';
import './HomeTab.css';

function HomeTab() {
  const spaceShipRef = useRef<HTMLImageElement>(null);

  useEffect(() => {
    console.log("running");
    const spaceShip = spaceShipRef.current;

    if (!spaceShip) {
      console.error('The spaceship element was not found.');
      return; // Stop execution if the element is not found
    }

    let hasTakenOff = false; // To prevent multiple takeoffs

    // Function to trigger the takeoff and stop shaking on the first scroll
    function handleScroll() {
      if (!hasTakenOff && spaceShip) {
        // Stop the shaking by removing the infinite shaking animation
        spaceShip.style.animation = 'none';

        // Start the take-off animation
        spaceShip.classList.add('takeOff');

        hasTakenOff = true; // Ensure takeoff only happens once
      }
    }

    // Detect scroll event and trigger takeoff
    window.addEventListener('scroll', handleScroll, { once: true }); // "once: true" ensures it runs only on the first scroll

    return () => {
      // Clean up the event listener when the component unmounts
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <div className="homeTab">
      <img className="homeBackground" src="homeBackground.png" alt=""></img>
      <img className="homeBackground" src="earth.png" alt=""></img>
      <img className="shadow" src="shadow.png" alt=""></img>
      <img className="sun" src="sun.png" alt=""></img>
      <img className="moon" src="moon.png" alt=""></img>
      <img ref={spaceShipRef} className="spaceShip" src="spaceShip.png" alt=""></img>
      <div className="borderUnderline">
        <div className="name">Isidore Mones.</div>
      </div>
      <div className="header">Engineering and Computer Science</div>
      <div className="homeBorderDiv">
        <img className="homeBorder" src="seafoamborder.png" alt=""></img>
      </div>
    </div>
  );
}

export default HomeTab;
